import React, { Component } from 'react';
import BarGraphCard from '../components/BarGraphCard.jsx';
import PieGraphCard from '../components/PieGraphCard.jsx';
import LineGraphCard from '../components/LineGraphCard.jsx';
import TweetsCard from '../components/TweetsCard.jsx';
import WordCloudCard from '../components/WordCloudCard.jsx';
import axios from 'axios';


class MentionStatistics extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {

      }
    }
  };

  componentDidMount = () => {
    this.getData();
  }

  getData = () => {
    const url = "https://easydoc.xyz/mock/CHs95kcg/api/mention";
    axios.get(url)
      .then(res => {
        console.log(res.data);
        this.setState({
          data: res.data
        })
      },
      error => console.log('An error occurred.', error),
    );
  }

  render() {
    const { data } = this.state;
    var scores = [];
    console.log(data);
    if (data && data.score_dist) {
      const score_dist = data.score_dist;
      scores = [
        {name: "positive", value: score_dist["positive"]},
        {name: "negative", value: score_dist["negative"]},
        {name: "neutral", value: score_dist["neutral"]},
      ]
    }

    const tmp_avg_scores = [
      {time: 'Jun', score: 100},
      {time: 'Jul', score: 200},
      {time: 'Aug', score: 300},
      {time: 'Sep', score: 200},
      {time: 'Oct', score: 400},
      {time: 'Nov', score: 100},
      {time: 'Dec', score: 200},
    ];

    return (
      <div>
        {!data ? null :
        <div className="AA-container">
          <BarGraphCard graphData={data.model_score_list} type="Sentiment Score for Models"/>
          <PieGraphCard graphData={scores} type="Sentiment Scores Distribution"/>
          <LineGraphCard graphData={tmp_avg_scores} type="Sentiment Scores over Time"/>

        </div>}
      </div>

    )
  }


}

// <TweetsCard tweets={data.top_twitters} type="Latest Mentions" />
// <WordCloudCard words={data.word_cloud} type="Word Cloud"/>


export default MentionStatistics;
