import React, { Component } from 'react';
import BarGraphCard from '../components/BarGraphCard.jsx';
import PieGraphCard from '../components/PieGraphCard.jsx';




class MentionStatistics extends Component {
  constructor(props) {
    super(props);
    this.state = {
      data: {
        sentimentScoresForDiffModels: [
          {time: 'XC40', followers: 100},
          {time: 'XC90', followers: 200},
          {time: 'Model1', followers: 300},
          {time: 'Model2', followers: 80},
        ],
        sentimentScores: [
          {name: 'Positive', value: 80},
          {name: 'Negative', value: 100},
          {name: 'Neutral', value: 50},
        ]
      }
    }
  };

  render() {
    const { data } = this.state;

    return (
      <div>
        <BarGraphCard graphData={data.sentimentScoresForDiffModels} type="Sentiment Score for Different Models"/>
        <PieGraphCard graphData={data.sentimentScores} type="Sentiment Scores in All Mentions"/>
      </div>

    )
  }


}


export default MentionStatistics;
