import React, { Component } from 'react';
import '../assets/css/Dashboard.css';
import {
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Label,
} from 'recharts';
import { Form } from 'react-bootstrap';


class AreaGraphCard extends Component {


  constructor(props) {
    super(props);
    this.state = {
      models: {
        "s60": true,
        "xc90": false,
        "xc40": false
      }
    }
  }

  toggle = (event) => {
    var models = this.state.models;
    const target = event.target
    const checked = target.checked
    const name = target.name
    models[name] = checked;
    this.setState({
      models: models,
    })
  }

  render() {

    const { graphData, type, isModel } = this.props;
    const { models } = this.state;

    const selectModel = () => {
      return (
        <div className="model-form">
        <Form >
          <Form.Check
            type="checkbox"
            id="checkbox"
            label="s60"
            name="s60"
            className="model-form-checkbox"
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            type="checkbox"
            id="checkbox"
            label="xc90"
            name="xc90"
            className="model-form-checkbox"
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            type="checkbox"
            id="checkbox"
            label="xc40"
            name="xc40"
            className="model-form-checkbox"
            onChange={e => this.toggle(e)}
          />
        </Form>
        </div>
      )
    }
    console.log(graphData);

    return (
      <div className="card-big-container">
        <div className="card-title">
          {type}
          {selectModel()}
        </div>
        <ResponsiveContainer width={310} height="75%" className="area-chart-container">
          <AreaChart data={graphData} margin={{top: 5, bottom: 5, left: -30}}>
           <XAxis dataKey="key"/>
           <YAxis/>
           <Tooltip/>
           {!models["s60"] ? null :  <Area type='monotone' dataKey="s60" stroke="#A7E1AD" fill="#A7E1AD" />}
           {!models["xc90"] ? null :  <Area type='monotone' dataKey="xc90" stroke="#A7E1AD" fill="#A7E1AD" />}
           {!models["xc40"] ? null :  <Area type='monotone' dataKey="xc40" stroke="#D9CCDF" fill="#A7E1AD" />}
          </AreaChart>
        </ResponsiveContainer>

      </div>
    )

  }

}


export default AreaGraphCard;
