import React, { Component } from 'react';
import SummaryCard from '../components/SummaryCard.jsx';
import BarGraphCard from '../components/BarGraphCard.jsx';
import PieGraphCard from '../components/PieGraphCard.jsx';
import axios from 'axios';


class AccountAnalytics extends Component {

  constructor(props) {
    super(props);
    this.state = {
      data: {
        summary_resp: {
          followers: 1000000,
          following: 20000,
          tweets: 150,
          likes: 333333
        },
         new_follower_count: [
                {time: 'Jun', followers: 100},
                {time: 'Jul', followers: 200},
                {time: 'Aug', followers: 300},
                {time: 'Sep', followers: 200},
                {time: 'Oct', followers: 400},
                {time: 'Nov', followers: 100},
                {time: 'Dec', followers: 200},
          ],
          new_comment_count: [
                 {time: 'Jun', followers: 100},
                 {time: 'Jul', followers: 200},
                 {time: 'Aug', followers: 300},
                 {time: 'Sep', followers: 200},
                 {time: 'Oct', followers: 400},
                 {time: 'Nov', followers: 100},
                 {time: 'Dec', followers: 200},
           ],
           new_like_count: [
                  {time: 'Jun', followers: 100},
                  {time: 'Jul', followers: 200},
                  {time: 'Aug', followers: 300},
                  {time: 'Sep', followers: 200},
                  {time: 'Oct', followers: 400},
                  {time: 'Nov', followers: 100},
                  {time: 'Dec', followers: 200},
            ],
            new_mention_count: [
                   {time: 'Jun', followers: 100},
                   {time: 'Jul', followers: 200},
                   {time: 'Aug', followers: 300},
                   {time: 'Sep', followers: 200},
                   {time: 'Oct', followers: 400},
                   {time: 'Nov', followers: 100},
                   {time: 'Dec', followers: 200},
             ],
            region_count_list: [
              {name: 'Region A', value: 200},
              {name: 'Region B', value: 100},
              {name: 'Region C', value: 300},
              {name: 'Region D', value: 240},
              {name: 'Region E', value: 160},
              {name: 'Region F', value: 90},
            ]
      }
    };
  }

  componentDidMount = () => {
    this.getData();
  }

  getData = () => {
    const url = "https://easydoc.xyz/mock/CHs95kcg/api/account";
    axios.get(url)
      .then(res => {
        console.log(res.data);
        this.setState({
          data: res.data
        })
      },
      error => console.log('An error occurred.', error),
    );
  }

  render() {

    const { data } = this.state;
    console.log(data);
    return (
      <div className="AA-container">
        <SummaryCard summary={data.summary_resp}/>
        <BarGraphCard graphData={data.new_follower_count} type="New Followers"/>
        <PieGraphCard graphData={data.region_count_list} type="Follower Region Distribution"/>
        <BarGraphCard graphData={data.new_comment_count} type="New Comments"/>
        <BarGraphCard graphData={data.new_like_count} type="New Likes"/>
        <BarGraphCard graphData={data.new_mention_count} type="New Mentions"/>
      </div>

    )
  }
}


export default AccountAnalytics;
