import React, { Component } from 'react';
import SummaryCard from '../components/SummaryCard.jsx';
import BarGraphCard from '../components/BarGraphCard.jsx';
import PieGraphCard from '../components/PieGraphCard.jsx';
import { Form } from 'react-bootstrap';
import axios from 'axios';


class AccountAnalytics extends Component {

  constructor(props) {
    super(props);
    this.state = {
      mode: 1,
    };
  }

  componentDidMount = () => {
    this.getData(1);
  }

  getData = (mode) => {
    // const url = "https://easydoc.xyz/mock/CHs95kcg/api/account/?duration_mode=" + mode;
    const url = "https://easydoc.xyz/mock/CHs95kcg/api/account";
    axios.get(url)
      .then(res => {
        console.log(res.data);
        this.setState({
          data: res.data,
          mode: mode,
        })
      },
      error => console.log('An error occurred.', error),
    );
  }

  toggle = (event) => {
    const target = event.target;
    const value = target.value;
    this.getData(value);
  }

  render() {

    const { data, mode } = this.state;
    var regions = []
    if (data && data.region_count_list) {
      data.region_count_list.map(item => {
        regions.push({
          name: item["country_code"],
          value: item["percentage"]
        })
      })
    }

    const selectMode = () => {
      return (
        <div className="aa-mode-form">
        <Form inline>
          <Form.Check
            inline
            type="radio"
            label="Year"
            name="Year"
            value="0"
            className="aa-mode-form-checkbox"
            checked={mode == 0}
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            inline
            type="radio"
            label="Month"
            name="Month"
            value="1"
            className="aa-mode-form-checkbox"
            checked={mode == 1}
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            inline
            type="radio"
            label="Week"
            name="Week"
            value="2"
            className="aa-mode-form-checkbox"
            checked={mode == 2}
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            inline
            type="radio"
            label="Day"
            name="Day"
            value="3"
            className="aa-mode-form-checkbox"
            checked={mode == 3}
            onChange={e => this.toggle(e)}
          />
        </Form>
        </div>
      )
    }


    return (
      <Form inline>
        <Form.Check
          inline
          type="radio"
          label="Year"
          name="Year"
          value="0"
          className="aa-mode-form-checkbox"
          checked={mode == 0}
          onChange={e => this.toggle(e)}
        />
        <Form.Check
          inline
          type="radio"
          label="Month"
          name="Month"
          value="1"
          className="aa-mode-form-checkbox"
          checked={mode == 1}
          onChange={e => this.toggle(e)}
        />
        <Form.Check
          inline
          type="radio"
          label="Week"
          name="Week"
          value="2"
          className="aa-mode-form-checkbox"
          checked={mode == 2}
          onChange={e => this.toggle(e)}
        />
        <Form.Check
          inline
          type="radio"
          label="Day"
          name="Day"
          value="3"
          className="aa-mode-form-checkbox"
          checked={mode == 3}
          onChange={e => this.toggle(e)}
        />
      </Form>

    )
  }
}


export default AccountAnalytics;
