import React, { PureComponent } from 'react';
import '../assets/css/Dashboard.css';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  Label,
} from 'recharts';


class BarGraphCard extends PureComponent {

  render() {

    const { graphData } = this.props;

    return (

      <div className="card-big-container">
        <div className="card-title"> New Followers </div>
        <ResponsiveContainer width={320} height="75%">
          <BarChart data={graphData} margin={{top: 5, bottom: 5}}>
           <CartesianGrid strokeDasharray="3 3"/>
           <XAxis dataKey="time"/>
           <YAxis/>
           <Tooltip/>
           <Bar dataKey="followers" fill="#A6CEE3" />
          </BarChart>
        </ResponsiveContainer>
      </div>
    )

  }

}



export default BarGraphCard;
