import React, { Component } from 'react';
import SummaryCard from '../components/SummaryCard.jsx';
import BarGraphCard from '../components/BarGraphCard.jsx';
import PieGraphCard from '../components/PieGraphCard.jsx';
import axios from 'axios';
import { Form } from 'react-bootstrap';


class AccountAnalytics extends Component {

  constructor(props) {
    super(props);
    this.state = {

    };
  }

  componentDidMount = () => {
    this.getData(1);
  }

  getData = (mode) => {
    const url = "https://easydoc.xyz/mock/CHs95kcg/api/account/?duration_mode=" + mode;
    axios.get(url)
      .then(res => {
        console.log(res.data);
        this.setState({
          data: res.data
        })
      },
      error => console.log('An error occurred.', error),
    );
  }

  toggle = (e) => {
    getData()
  }

  render() {

    const { data } = this.state;
    var regions = []
    if (data && data.region_count_list) {
      data.region_count_list.map(item => {
        regions.push({
          name: item["country_code"],
          value: item["percentage"]
        })
      })
    }

    const selectMode = () => {
      return (
        <div className="mode-form">
        <Form >
          <Form.Check
            inline
            type="radio"
            label="Year"
            name="Year"
            value="0"
            className="aa-mode-form-checkbox"
            checked={mode == "top_like_twitters"}
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            inline
            type="radio"
            label="Month"
            name="Month"
            value="1"
            className="aa-mode-form-checkbox"
            checked={mode == "top_retweet_twitters"}
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            inline
            type="radio"
            label="Week"
            name="Week"
            value="2"
            className="aa-mode-form-checkbox"
            checked={mode == "recent_twitters"}
            onChange={e => this.toggle(e)}
          />
          <Form.Check
            inline
            type="radio"
            label="Day"
            name="Day"
            value="3"
            className="aa-mode-form-checkbox"
            checked={mode == "recent_twitters"}
            onChange={e => this.toggle(e)}
          />
        </Form>
        </div>
      )
    }


    return (
      <div>
        {selectModel()}
        {!data ? null :
        <div className="AA-container">
          <SummaryCard summary={data.summary_resp}/>
          <PieGraphCard graphData={regions} type="Follower Region Distribution"/>
          <BarGraphCard graphData={data.new_follower_count} type="New Retweets"/>
          <BarGraphCard graphData={data.new_comment_count} type="New Comments"/>
          <BarGraphCard graphData={data.new_like_count} type="New Likes"/>
          <BarGraphCard graphData={data.new_mention_count} type="New Mentions"/>
        </div> }
      </div>

    )
  }
}


export default AccountAnalytics;
